tasks.command:
npm install --save-dev vite && npm install react-refresh @vitejs/plugin-react-refresh && npm uninstall react-scripts && mv public/index.html public/index.html.bak && cp public/index.html.bak index.html && rm -rf build && rm -rf public && echo '{
  "plugins": ["@vitejs/plugin-react-refresh"]
}' > vite.config.js
